<script setup>
  import { ref, onMounted } from 'vue';
  import * as d3 from 'd3';
  import FlowerView from '@/components/flowerView/FlowerView.vue'

  const mapContainer = ref(null)

  const drawMap = () => {
    const width = 1900;
    const height = 700;

    const svg = d3.select(mapContainer.value)
      .append('svg')
      .attr('width', width)
      .attr('height', height);

    // svg.append('rect')
    //   .attr('width', width)
    //   .attr('height', height)
    //   .attr('fill', 'red');  // 设置背景颜色为红色
    //

    fetch('/tree.svg')
      .then(response => response.text())
      .then(svgText => {
        console.log('Loaded tree.svg:', svgText);

        const parser = new DOMParser();
        const xmlDoc = parser.parseFromString(svgText,'image/svg+xml');
        const treeGroup = xmlDoc.querySelector('#tree');

        if (treeGroup){

          console.log("svg:", svg.node());

          svg.node().appendChild(treeGroup);

          d3.select(treeGroup)
            // .attr('transform','translate(520,360)scale(0.3)')
            .attr('transform','translate(140,80)scale(1.6)')
            .attr('stroke','black')
            .attr('stroke-width',1)
            .attr('fill','#662800');
        }else {
          console.log('tree group not found in SVG');
        }
      })
      .catch(error => {
        console.error('Error loading tree SVG',error);
      });
  };

  onMounted(() => {
    drawMap();
  })
</script>

